export default class Ref4 extends Component {
    constructor(props) {
      super(props)
    
      this.createElement= React.createRef();
    
    }
    componentDidMount(){
        console.log(this.createElement);
    }
  render() {
    return (
      <>
        <div className='box1' ref={this.createElement}></div>
      </>
    )
  }
}
