import React, { Component } from 'react'
// import Img from "./"

export class ErrorBoundaries extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         hasError: false
      }
    }
    static getDerivedStateFromError(error){
        return{
            hasError : true
        }
    }
  render() {
    if (this.state.hasError) {
        return (
            <h1>Something Went Wrong!!!</h1>
        )
    }else{
        return(
            this.props.children
        )
    }
  }
}

export default ErrorBoundaries
