import React, { Component } from 'react'

export default class ClassClick extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         message: "१ मछली पानी मे गई",
      }
    }
    changeMessage(){
        this.setState={
            message:"छपाक"
        }
    }
  render() {
    let {message} =this.state;
    return (
      <div>
        <h1>{message}</h1>
        <button onClick={changeMessage}>Change</button>
      </div>
    )
  }
}
