import React, { Component } from 'react';
import axios from 'axios';
import './todolist.css';
import TodoItems from './TodoItems';

export default class Task4 extends Component {
  constructor(props) {
    super(props);

    this.state = {
      tasks: [], // Use 'tasks' instead of 'task' for consistency
      newTask: '',
      updatedTask: {},
    };
  }

  onChangeHandle = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  componentDidMount() {
    this.getTasks(); // Use a clearer function name
  }

  getTasks = async () => { // Use async/await for cleaner API calls
    try {
      const response = await axios.get('https://jsonplaceholder.typicode.com/posts');
      this.setState({ tasks: response.data });
    } catch (error) {
      console.error('Error fetching tasks:', error);
      alert('Failed to load tasks. Please check your network connection or API endpoint.');
    }
  };

  onSubmit = async () => {
    const { newTask } = this.state;

    try {
      const response = await axios.post('https://jsonplaceholder.typicode.com/posts', {
        title: newTask,
        completed: false, // Add 'completed' property as per API requirements
      });
      console.log('Task created:', response.data);
      this.setState({ newTask: '', completed: false });
      this.getTasks(); // Refresh tasks after creation
    } catch (error) {
      console.error('Error creating task:', error);
      alert('Failed to create task. Please try again.');
    }
  };

  onDelete = async (id) => {
    try {
      await axios.delete(`https://jsonplaceholder.typicode.com/posts/${id}`);
      console.log('Task deleted (id:', id, ')');
      this.getTasks(); // Refresh tasks after deletion
    } catch (error) {
      console.error('Error deleting task:', error);
      alert('Failed to delete task. Please try again.');
    }
  };

  onUpdate = async (task) => {
    try {
      const updatedTask = { ...task, completed: !task.completed }; // Toggle completion status
      const response = await axios.put(`https://jsonplaceholder.typicode.com/posts/${task.id}`, updatedTask);
      console.log('Task updated:', response.data);
      this.getTasks(); // Refresh tasks after update
    } catch (error) {
      console.error('Error updating task:', error);
      alert('Failed to update task. Please try again.');
    }
  };

  render() {
    const { tasks, newTask } = this.state;

    return (
      <>
        <div className="main">
          <h1>Todo-list</h1>
          <form action="" onSubmit={this.onSubmit}>
            <div className="input">
              <input onChange={this.onChangeHandle} type="text" value={newTask} name="newTask" placeholder="Add a new task" />
              <button className="button">+</button>
            </div>
          </form>
          <table>
            <thead>
              <tr>
                <th>View</th>
                <th>Update</th>
                <th>Delete</th>
              </tr>
            </thead>
            <tbody>
              {tasks.map((task) => (
                <TodoItems key={task.id} task={task} onDelete={this.onDelete} onUpdate={this.onUpdate} />
              ))}
            </tbody>
          </table>
        </div>
      </>
    );
  }
}
