import React from 'react';
import { useState } from 'react';
import Menu from './menu';

export const Product = () => {
    const [items, setItems] = useState(Menu);

    return (
        <div>
            <h1 className="text-center">Order Your Favorite Food</h1>
            <div className="container d-flex justify-content-between">
                <button className="btn btn-warning">Breakfast</button>
                <button className="btn btn-warning">Lunch</button>
                <button className="btn btn-warning">Evening</button>
                <button className="btn btn-warning">Timepass</button>
                <button className="btn btn-warning">Dinner</button>
                <button className="btn btn-warning">All</button>
            </div>
            <div className="container-fluid">
                <div className="row">
                    {
                        items.map((item) => (
                            <div className="item-card col-8 my-5 d-flex mx-auto border border-2 rounded rounded-4" key={item.id}>
                                <div className="col-4 my-5">
                                    <img srcod src={item.image} alt={item.name} className="rounded rounded-3" style={{ height: "200px", width: "200px" }} />
                                </div>
                                <div className="col-8">
                                    <ul className="list-group text-start my-5" data-bs-theme="dark">
                                        <li className="list-group-item list-group-item-action">Name: {name}</li>
                                        <li className="list-group-item list-group-item-action">Category: {item.category}</li>
                                        <li className="list-group-item list-group-item-action text-danger">Price: {item.price}</li>
                                        <li className="list-group-item list-group-item-action">Description: {item.description}</li>
                                        <li className="list-group-item list-group-item-action">Product ID: {item.id}</li>
                                    </ul>
                                    <button className='btn btn-primary mb-4'>Order</button>
                                </div>
                            </div>
                        ))
                    }
                </div>
            </div>
        </div>
    );
};
