import React, { Component } from 'react'

export default class State3 extends Component {
    constructor(props) {
      super(props)
    
      this.state = {
         user: this.props.user,
      }
    }
    changeUser(){
        this.setState({
            user: "Ganpatrao"
        })
    }
  render() {
    return (
      <div>
        <h1>User is {this.state.user}</h1>
        <button></button>
      </div>
    )
  }
}
